mapper.insert:
INSERT INTO javabucks_card (
	cardRegNum, userId, cardName, cardPrice
)
VALUES (
	#{cardRegNum}, #{userId}, #{cardName}, 0
)

mapper.select:
SELECT alarmNum, userId, alarmIsRead, alarmCate, alarmCont, TO_CHAR(alarmRegDate, 'YYYY-MM-DD HH24:MI:SS') AS alarmRegDate FROM javabucks_alarm WHERE userId = #{userId}
AND alarmCate = 'cpn' OR alarmCate = 'grade'
AND alarmCate = 'order' OR alarmCate = 'pickUp'
AND alarmCate = #{alarmCate}
ORDER BY alarmRegDate DESC

