mapper.select:

SELECT * FROM 
( SELECT ROWNUM AS rnum, R.* FROM ( SELECT * FROM javabucks_admin ORDER BY adminJoindate DESC, adminId ASC ) R 
WHERE ROWNUM <= #{endIndex} ) WHERE rnum > #{startIndex}

SELECT * FROM ( SELECT ROWNUM AS rnum, a.* FROM ( SELECT adminId, adminPasswd, adminEmail, adminAuthority, adminJoindate, adminEnable FROM javabucks_admin WHERE 1 = 1
AND adminId LIKE '%' || #{adminId} || '%'
AND adminEmail LIKE '%' || #{adminEmail} || '%'
AND adminAuthority = #{authority}
AND adminEnable = #{enable}
AND adminJoindate >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND adminJoindate <= TO_DATE(#{endDate}, 'YYYY-MM-DD')
ORDER BY adminJoindate DESC ) a WHERE ROWNUM <= #{endIndex} ) WHERE rnum >= #{startIndex}

mapper.update:
UPDATE javabucks_admin
SET adminEnable = 'N'
WHERE adminId = #{adminId}

mapper.select:

