where bucksId = #{bucksId}


		and TO_CHAR(orderDate, 'YYYY-MM-DD') = #{searchOpt_orderDate}
		and orderCode = #{searchOpt_orderCode}


		and TO_CHAR(orderDate, 'YYYY-MM-DD') = #{searchOpt_orderDate}


		and orderCode = #{searchOpt_orderCode}




		and orderType = 'order'
order by orderDate desc

